-- classDiagram
--     B_1 <|-- B_2
--     B_2 <|-- B_3
--     B_2 <|-- B_4
CREATE TABLE B_1 (
    PK_11 INT64 NOT NULL,
) PRIMARY KEY (PK_11);

CREATE TABLE B_2 (
    PK_11 INT64 NOT NULL,
    PK_21 INT64 NOT NULL,
) PRIMARY KEY (PK_11, PK_21),
    INTERLEAVE IN PARENT B_1 ON DELETE CASCADE;

CREATE TABLE B_3 (
    PK_11 INT64 NOT NULL,
    PK_21 INT64 NOT NULL,
    PK_31 INT64 NOT NULL,
) PRIMARY KEY (PK_11, PK_21, PK_31),
    INTERLEAVE IN PARENT B_2 ON DELETE CASCADE;

CREATE TABLE B_4 (
    PK_11 INT64 NOT NULL,
    PK_21 INT64 NOT NULL,
    PK_41 INT64 NOT NULL,
) PRIMARY KEY (PK_11, PK_21, PK_41),
    INTERLEAVE IN PARENT B_2 ON DELETE CASCADE;
